fn get(lines: &Vec<Vec<char>>, x: i32, y: i32) -> char {
    assert!(x >= 0 && y >= 0 && y < lines.len() as i32 && x < lines[y as usize].len() as i32);
    lines[y as usize][x as usize]   
}

fn tasks(input: &str) -> (String,i32) {
    let lines = input.lines().map(|line| line.chars().collect::<Vec<_>>()).collect::<Vec<_>>();

    let mut x = 0i32;
    let mut y = 0i32;

    for _ in 0..lines[0].len() {
        if get(&lines, x, 0) == '|' {
            break;
        }
        x += 1;
    }

    let mut stations = String::new();

    let mut dx = 0;
    let mut dy = 1;
    let mut n = 0i32;

    loop {
        let ch = get(&lines, x, y);

        if ch == ' ' {
            break;
        }

        if ch.is_alphabetic() { 
            stations.push(ch);                             
        }        

        if ch == '+' {            
            match (dx, dy) {
                (0, _) => {
                    if x < 1 || get(&lines, x - 1, y) == ' ' {
                        dy = 0;
                        dx = 1;
                    } else {
                        assert!(x + 1 >= lines[y as usize].len() as i32 || get(&lines, x + 1, y) == ' ');
                        dy = 0;
                        dx = -1;
                    }
                },
                (_, 0) => {
                    if y < 1 || get(&lines, x, y - 1) == ' ' {
                        dy = 1;
                        dx = 0;
                    } else {
                        assert!(y + 1 >= lines.len() as i32 || get(&lines, x, y + 1) == ' ');
                        dy = -1;
                        dx = 0;
                    }
                },      
                _ => { 
                    panic!("We not supposed to get here 3.");      
                }
            }
        }

        x += dx;
        y += dy;
        n += 1;
    }
    
    (stations, n)
}

